import './donation.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  faExclamationTriangle, faUser } from '@fortawesome/free-solid-svg-icons';
import '@fortawesome/fontawesome-free/css/all.min.css';
import React, { useState } from 'react';

const Donation = ({ 
  pictureUrl,
  title,
  username,
  amount,
  target,
  description
}) => {
  const amountPercentage = (amount * 100) / target;
  const [showShareModal, setShowShareModal] = useState(false);
  const [showReportModal, setShowReportModal] = useState(false);


  const openShareModal = () => {
    setShowShareModal(true);
  };

  const closeShareModal = () => {
    setShowShareModal(false);
  };

  const openReportModal = () => {
    setShowReportModal(true);
  };

  const closeReportModal = () => {
    setShowReportModal(false);
  };

 <head><link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css" integrity="sha512-i1gXTVHAeNm/bnAn4fopV5cyRZdY8bmqSvwRDC2LF+3yAJeQ2nJ/CsOPu9mJs/gvf4AGXoGgZTx4iyRSyUlH/w==" crossorigin="anonymous" referrerpolicy="no-referrer" />
  </head>
  
  
  return (
  
    <div className="post-container">

      <div className='lfog'>
      <div className="left-container">
        <img src={pictureUrl} alt="There is no picture!" className="valid-picture" />
      </div>
      <div className="right-container">
        <div className="post-info">
          <h3 className="post-title">{title}</h3>
          <div className="user-info">
          <FontAwesomeIcon className='user-icon' icon={faUser} />
           
            <span className="username" style={{fontSize:'0.8rem', color:'rgba(2, 48, 71, 1)', fontWeight:'bold'}} >{username}</span>
          </div>
          <div className="amount-info">
            <span className="amount" style={{fontSize:'0.7rem', color:'rgba(2, 48, 71, 1)', fontWeight:'bold'}}> {amount} $</span>
            <span className="target" style={{fontSize:'0.7rem', color:'rgba(2, 48, 71, 1)'}}> ( The target is to raise {target} $)</span>
          </div>
          <div className="progress-bar">
            <div className="progress" style={{ width: `${amountPercentage}%` }}></div>
          </div>
        </div> 
        <div className="actions">
           <button className="donate-btn" > <a href='/donate'>
            
          <span style={{color:'white', fontSize:'1rem',textTransform: 'capitalize', fontWeight:'bold'}}>Donate now</span>
          </a></button>
      
          <button className="share-btn" onClick={openShareModal}>
        <i className="fa-solid fa-share-from-square" style={{ color: 'rgba(33, 158, 188, 1)', fontSize: '1.5em' }}></i>
        <span style={{ color: 'rgba(33, 158, 188, 1)', textTransform: 'capitalize', fontSize: '1rem', fontWeight: 'bold' }}>Share</span>
      </button>
      <button className="report-btn" onClick={openReportModal}>
        <FontAwesomeIcon icon={faExclamationTriangle} style={{ color: 'rgba(136, 15, 51, 1)', fontSize: '1.5em' }} />
        <span style={{ color: 'rgba(136, 15, 51, 1)', fontSize: '1rem', textTransform: 'capitalize', fontWeight: 'bold' }}>Report</span>
      </button>

      {/* Modal for Share */}
      {showShareModal && (
        <div id="shareModal" className="modal">
          <div className="contenuu">
            <span className="close" onClick={closeShareModal}>&times;</span>
            <p>Add share options here</p>
            {/* Add share options or content here */}
          </div>
        </div>
      )}

      {/* Modal for Report */}
      {showReportModal && (
        <div id="reportModal" className="modal">
          <div className="contenuu">
            <span className="close" onClick={closeReportModal}>&times;</span>
            <p>Report form goes here</p>
            {/* Add report form or content here */}
          </div>
        </div>
      )}
        </div> 
        </div>
      </div>
      <div className="description-container">
        <h2 className="description-title" style={{color:'rgba(2, 48, 71, 1)', fontWeight:'bold', marginBottom:'7px'}} >Description</h2>
            <p className="description" style={{color:'rgba(2, 48, 71, 1)', fontSize:'1.1rem', fontWeight:'lighter'}}>{description}</p>
      </div>
 
     
    </div>
  );
}

export default Donation;
