import React, { useState } from 'react';
import { FaUser, FaDollarSign, FaShare, FaExclamationTriangle } from 'react-icons/fa';
import "./donation.css"
function Donation() {
  // Define state variables for donation amount and target

  const [amount, setAmount] = useState(5000);
  const [target, setTarget] = useState(30000);

  // Function to handle donation
  const handleDonateNow = () => {
    // Code to navigate to another page for donation
  };

  // Function to handle sharing
  const handleShare = () => {
    // Code to show share window
  };

  // Function to handle reporting
  const handleReport = () => {
    // Code to show report window
  };

  return (
    <div className="donation-container">
      {/* First Component */}
      <div className="donation-left">
        {/* Picture */}
        <img src="path_to_picture" alt="Pot Picture" />
      </div>
      <div className="donation-right">
        {/* Title */}
        <h2>Donation Title</h2>
        {/* Username */}
        <div className="username">
          <FaUser color="blue" />
          <p>Username</p>
        </div>
        {/* Amount */}
        <div className="amount">
          <p>{amount}</p>
          <FaDollarSign />
        </div>
        {/* Target */}
        <p>The target is to raise {target} $</p>
        {/* Range */}
        <div className="range" style={{ width: `${(amount / target) * 100}%`, backgroundColor: 'orange' }}></div>
        {/* Buttons */}
        <div className="buttons">
          <button onClick={handleDonateNow} className="red-button">Donate Now</button>
          <button onClick={handleShare} className="blue-button"><FaShare /> Share</button>
          <button onClick={handleReport} className="red-button"><FaExclamationTriangle /> Report</button>
        </div>
      </div>

      {/* Second Component: Share Window */}
      {showShareWindow && (
        <div className="share-window">
          {/* Share window content */}
        </div>
      )}

      {/* Third Component: Report Window */}
      {showReportWindow && (
        <div className="report-window">
          {/* Report window content */}
        </div>
      )}

      {/* Fourth Component */}
      <div className="description">
        {/* Title */}
        <h2>Description</h2>
        {/* Paragraph */}
        <p>Paragraph Text</p>
      </div>
    </div>
  );
}

export default Donation;
